mapper.update:
update manuscript_data
set love = #{love,jdbcType=INTEGER},
  forward = #{forward,jdbcType=INTEGER},
  comment = #{comment,jdbcType=INTEGER},
  collection = #{collection,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, love, forward, comment, collection
from manuscript_data
where id = #{id,jdbcType=INTEGER}
